import { DataTypes, NDArray } from '../../ndarray';
import { SumTypes } from '../../types';
import { KernelInputConfig, KernelNode, TapeNodeInputArrays, TapeNodeInputGradientArrays } from '../tape_types';
export interface SumNode<T extends keyof DataTypes> extends KernelNode {
    inputAndArgs: SumInputConfig<T>;
    output: NDArray<SumTypes[T]>;
    gradient: (dy: NDArray<SumTypes[T]>, y: NDArray<SumTypes[T]>) => SumGradientInputArrays<T>;
}
export interface SumInputConfig<T extends keyof DataTypes> extends KernelInputConfig {
    inputs: SumInputArrays<T>;
    args: {
        axes: number[];
    };
}
export interface SumInputArrays<T extends keyof DataTypes> extends TapeNodeInputArrays {
    x: NDArray<T>;
}
export interface SumGradientInputArrays<T extends keyof DataTypes> extends TapeNodeInputGradientArrays {
    x: () => NDArray<T>;
}
